INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (20, 18, N'海豹六隊第一季', N'歐美劇', N'2017', N'美國', N' 巴裡·斯隆 凱爾·施密德 胡安帕布魯拉巴 艾德文· 萊斯利·林卡·格拉特', N'邁克爾·塞洛蒙', N'於美國時間1月18日首播的《海豹六隊 Six》，這部被預訂8集的劇由奧斯卡提名兼前越南老兵William Broyles，與他的兒子David Broyles所執筆，背景為美國海軍海豹突擊隊第六分隊，劇集講述他們在14年成功擊殺一個在阿富汗的塔利班領導人後，卻發現有美國公民與恐怖份子合作。這劇是根據現實發生過的任務改篇，每季的地點皆不同，首季定於非洲。地點是根據非洲現實的學生綁架案得到靈感。《國土安全 Homeland》的導演Lesli Linka Glatter將會執導該劇頭兩集。Barry Sloane飾演John Graves，綽號Bear，海豹六隊新任隊長，希望能在家庭﹑部隊﹑個人之間', N'https://cdn.99itv.net/upload/vod/20200510-1/159d7653d2b48eb4f357f6916f007e84.jpg', 10);
