INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (80, 135, N'明月幾時有', N'劇情片', N'未知', N'大陸', N' 周迅 彭於晏 霍建華', N'許鞍華', N'　《明月幾時有》取材於真實的曆史事件與曆史人物，以傳奇女性「方姑」為代表的仁人誌士在1940年代的香港展開生死救援，展現香港淪陷下的平凡人抗爭故事。', N'https://cdn.99itv.net/upload/vod/20200510-1/a351a6cb9f5d8bf95d62b64f9f8d08e2.jpg', 10);
