INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (113, 91, N'蕩寇風雲', N'動作片', N'2017', N'大陸', N' 趙文卓 洪金寶 萬茜 小出惠介', N'陳嘉上', N'影片講述了一代名將戚繼光南征倭寇還沿海太平的傳奇故事，明末年間地方官府腐敗、明兵軍心不整，倭寇趁虛而入，屢次進犯東南沿海。正值內憂外患之際，戚繼光（趙文卓 飾）臨危受命，與俞大猷（洪金寶 飾）自行招募三千義烏村民，在短短數日之內，以嚴明軍紀、非常手段操練新兵，以先進武器、精絕陣法武裝部隊，成為史上首支「特種部隊」、聞名中外的「戚家軍」。', N'https://cdn.99itv.net/upload/vod/20200510-1/24458c654814ee5c8f5c7a5c7fe0d2e2.jpg', 0);
