INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (185, 158, N'亞人醬有話要說', N'動漫', N'2017', N'日本', N' 諏訪部順一 本渡楓 篠田南 夏川椎菜 日笠', N'安籐良', N'魅魔、杜拉罕、雪女，以及吸血鬼—— 她們是和我們人類稍微有點不同的「亞人」，近來被稱呼為「Demi」。 這些充滿個性的亞人們與對她們充滿興趣的高中老師·高橋鐵男， 即將展開一段治癒又有些刺激的校園亞人喜劇！', N'https://cdn.99itv.net/upload/vod/20200510-1/9795d8b2382c26efa647815c68e62ee6.jpg', 0);
