INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (187, 220, N'班長大人', N'陸劇', N'未知', N'大陸', N' 李凱馨 黃俊捷 戴景耀 鄧欣 陸妍淇', N'沈文帥', N'《班長大人》改編自超人氣同名漫畫，而該漫畫在網絡上點擊率超過千萬，由偶像演員傾情加盟，上演了一段熱血的青春故事。', N'https://cdn.99itv.net/upload/vod/20200510-1/a912600f28bbfbc8967a6623d10c2119.jpg', 0);
