INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (410, 466, N'冰川鯊魚', N'動作片', N'未知', N'美國', N' 愛德華·德魯特', N'艾米莉·史密斯', N'一種新型的鯊魚襲擊了北極科考站，科考站開始分崩離析。生存下來的倖存者必須製造武器以求自衛.', N'https://cdn.99itv.net/upload/vod/20200510-2/dde1c20d79a81caac28df22e7d49e5fb.jpg', 6);
