INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (413, 387, N'杜麗娘', N'愛情片', N'未知', N'大陸', N' 陳婧涵 李雲鶴 李琳琳 劉凡 陽春', N'周德新', N'電影講述了明朝南安太守杜寶之女杜麗娘遊走後花園，夢到有一位年輕書生手持半枝垂柳前來求愛,兩人在牡丹亭幽會，並在夢中相愛。於此同時書生柳夢梅也夢見在一座花園的梅樹下立著一位佳人,說同他有姻緣之份,從此踏上了尋夢之路。', N'https://cdn.99itv.net/upload/vod/20200510-2/25bb9f4ed3bcd06ce3f121e47e052f79.jpg', 0);
