INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (455, 433, N'反擊第六季', N'歐美劇', N'2017', N'美國', N' 沙利文·斯特普爾頓 菲利普·溫徹斯特 Dan', N'Perciv', N'反擊是一部於全球取景的動作驚悚好戲，劇情均由近年頭條新聞改編而來。該劇從機密情報機構——20號分部中的兩名成員著眼：來自英國反恐精銳部隊的軍官邁克爾·斯頓布奇中士，以及於美國進軍伊拉克前夜被開除軍籍的前三角洲部隊士兵達米恩·斯科特。斯頓布奇、斯科特以及20號分部的其他成員為了尋找臭名昭著的恐怖分子拉提夫的下落而踏遍全球，而拉提夫正在策劃一場使用大規模殺傷性武器的恐怖襲擊。', N'https://cdn.99itv.net/upload/vod/20200510-2/6d3a5a255a05d1333a88f049aa08659a.jpg', 4);
