INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (490, 468, N'王牌總裁霸王花', N'愛情片', N'未知', N'大陸', N' 張已桂 謝銘宇', N'馮哲', N'富商溫如虎得罪了人，為了保證自己的獨女溫婉不受傷害，於是派自己泰國兄弟曆開石的女兒十三媚回國保護，同時和霸道總裁梁山簽了保險合同，保護溫婉的安全。其實這是曆開石和溫如虎的計謀，他們利用溫婉來騙取梁山的違約保費，沒想到十三媚和梁山相處發生感情，溫和曆兩兄弟也因為分贓不均翻船…', N'https://cdn.99itv.net/upload/vod/20200510-2/e4876e15fc3801f3592b7880b8447f28.jpg', 0);
