INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (565, 549, N'肥皂泡', N'劇情片', N'未知', N'日本', N' 林遣都 市原悅子 籐井美菜', N'東伸兒', N'　　正因病住院的另一位主演市原悅子則通過語音表達了自己的心情。影片改編自乃朝南獲直木獎的同名暢銷小說，講述一個自暴自棄的年輕人在逃亡過程中重獲新生的故事。影片將於3月4日登陸日本院線。', N'https://cdn.99itv.net/upload/vod/20200510-2/f1497b87f7499cf620f7fd96aabab1a4.jpg', 10);
