INSERT INTO testDB.dbo.movieApp_movie (id, mid, title, types, years, country, actor, director, m_intro, poster, rating) VALUES (642, 627, N'梁山伯與祝英台新傳', N'陸劇', N'2017', N'大陸', N' 陳夢希 晏紫東 林楓松', N'劉國權', N'祝公遠為女兒祝英台與馬文才定了親，祝英台為試探馬文才的才華與其互通書信，不想卻發現端倪，為一探究竟祝英台女扮男裝去蕭山書院，不想在路上遇到出來採購的梁山伯，祝英台和梁山伯二人一見如故，一同解決了「碰瓷事件」後一同到了書院……當祝英台在陰差陽錯之下發現馬文才書信的秘密時，祝英台、梁山伯、馬文才三人之間的愛恨情愁隨之展開，千古悲劇即將上演…', N'https://cdn.99itv.net/upload/vod/20200510-3/304dfe7f13226d777d5992022c3200d1.jpg', 10);
